import React, { useState, useRef } from 'react';

const CreatorPage = () => {
  const [cardDescription, setCardDescription] = useState('');
  const textareaRef = useRef(null);

  const handleInput = (e) => {
    setCardDescription(e.target.value);
    // Ajusta la altura del textarea automáticamente
    textareaRef.current.style.height = "auto";
    textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
  };

  return (
    <div className="flex h-screen">
      {/* Columna Izquierda */}
      <div className="w-1/2 bg-white p-8 flex flex-col justify-center h-full overflow-y-auto">
        <h2 className="text-xl font-semibold mb-4 text-gray-700">Create Your Card</h2>
        <div className="space-y-4">
          <div>
            <label className="block text-gray-700 mb-1">Card Description</label>
            <textarea
              ref={textareaRef}
              value={cardDescription}
              onChange={handleInput}
              className="w-full px-3 py-2 border border-gray-300 rounded focus:outline-none focus:ring focus:ring-green-200"
              placeholder="Enter card description"
              rows="4"
              style={{ resize: "none", overflow: "hidden" }}
            />
          </div>
        </div>
      </div>

      {/* Columna Derecha */}
      <div className="w-1/2 bg-gray-100 p-8 flex items-center justify-center h-full">
        <div className="w-3/4 h-3/4 bg-white rounded-lg shadow-md flex items-center justify-center">
          <p className="text-gray-500">Card Preview</p>
        </div>
      </div>
    </div>
  );
};

export default CreatorPage;
